        return (
            <div>
                <input 
                    type='text'
                    placeholder='enter title'
                    maxLength='100'
                    onChange={(e) => this.updateTitle(e.target.value)}
                />
                <br/>
                <p>Upload header Image:</p>
                <input type='file'/>                
                <hr/>
                <Preview 
                    currTitle={this.state.articleTitle}
                />
            </div>
        )
    }
}

function Preview (props){
    return (
        <div>
            <h1>Preview</h1>
            <h2>title : {props.currTitle}</h2>
            
        </div>
    )
}
