import React from "react"

export default class SignupForm extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      firstname: "",
      lastname: "",
      email: "",
      password: "",
    }
    
    this.handleTyping = this.handleTyping.bind(this)
  }

  handleTyping(value, property){
    this.setState({
      [property]: value,
    },() => console.log(this.state))
    
  }

  render(){
    return(
      <div>
        <form>
          First Name: <br/>
          <input
            type="text"
            value={this.state.firstname}
            onChange={(e) => this.handleTyping(e.target.value,"firstname")}
          />
          <br/>Second Name: <br/>
          <input
            type="text"
            value={this.state.lastname}
            onChange={(e) => this.handleTyping(e.target.value,"lastname")}
          />
          <br/>Email: <br/>
          <input
            type="text"
            value={this.state.email}
            onChange={(e) => this.handleTyping(e.target.value,"email")}
          />
          <br/>Password: <br/>
          <input
            type="password"
            onChange={(e) => this.handleTyping(e.target.value,"password")}
          />
          <br/>
          <button onClick={() => this.props.handleSubmit(this.state)}>Submit</button>
        </form>
      </div>
    )
  }
}
