import React from "react"

export default class ArticleCreation extends React.Component{
    // When the article creation component is created it 
    // has a state of nothing
    constructor(props){
        super(props)
        this.state = {
            articleTitle: '',
        }

        this.updateTitle = this.updateTitle.bind(this)

    }
    
    updateTitle(value) {
        this.setState({
            articleTitle: value 
        })
    }
    render(){
        return (
            <div>
                <input 
                    type='text'
                    placeholder='enter title'
                    onChange={(e) => this.updateTitle(e.target.value)}
                />
                <input type='text' placeholder='enter image'/>                
                <hr/>
                <Preview 
                    currTitle={this.state.articleTitle}
                />
            </div>
        )
    }
}

function Preview (props){
    return (
        <div>
            <h1>Preview</h1>
            <h2>title : {props.currTitle}</h2>
            {console.log("PROPS>TITLE " + props.currTitle)}
        </div>
    )
}
