import React from 'react'

export default class ProfilePage extends React.Component {
  constructor(props){
    super(props)
    const userInfo = JSON.parse(localStorage.getItem("OPuserInfo"))
    this.state = {
      userInfo: userInfo,
    }
  } 
 

  render(){
    return(
      <div>
        <h1>{this.state.userInfo.firstname} {this.state.userInfo.lastname}</h1>
        <ProfileBio bio = {this.state.userInfo.bio}/>
      </div>
    )
  }
}

function ProfileBio(props) {
  if(typeof props.bio === null){
    return (
      <div>
        <label>Your Bio</label>
        <p>-- Your bio is undefined, select 'Edit' below to add a bio --</p>
      </div>
    )
  }
  else{
    return(
      <div>
        <label>Your Bio</label>
        <p>{props.bio}</p>
      </div>
    )
  }
}
