import React from 'react'

export default class ProfileBio extends React.Component {

  render(){
    if(this.props.bio === null){
      return (
        <div>
          <label>Your Bio</label>
          <p>-- Your bio is undefined, select 'Edit' below to add a bio --</p>
          <button>Edit</button>
        </div>
      )
    }
    else{
      return(
        <div>
          <label>Your Bio</label>
          <p>{this.stateprops.bio}</p>
        </div>
      )
    }
  }
  
}
